#!/bin/bash

# Create button click sound
cat > button-click.mp3.base64 << 'EOL'

EOL

# Create card flip sound
cat > card-flip.mp3.base64 << 'EOL'

EOL

# Create match success sound
cat > match-success.mp3.base64 << 'EOL'

EOL

# Create level complete sound
cat > level-complete.mp3.base64 << 'EOL'

EOL

# Create game over sound
cat > game-over.mp3.base64 << 'EOL'

EOL

# Convert base64 to mp3 files
base64 -d button-click.mp3.base64 > button-click.mp3
base64 -d card-flip.mp3.base64 > card-flip.mp3
base64 -d match-success.mp3.base64 > match-success.mp3
base64 -d level-complete.mp3.base64 > level-complete.mp3
base64 -d game-over.mp3.base64 > game-over.mp3

# Clean up base64 files
rm *.base64

echo "Sound files created successfully!" 